import { useFormik } from 'formik';
import React from 'react';
import DatePicker from 'react-datepicker';
import './App.css';
import 'react-datepicker/dist/react-datepicker.css';
import * as yup from 'yup';

const schema = yup.object({
  email: yup.string().email().required(),
  date: yup.date().min(new Date()).required('date is required'),
});

function App() {
  const formik = useFormik({
    initialValues: {
      email: '',
      date: null,
    },
    validationSchema: schema,
    onSubmit: (values) => console.log(values),
  });

  return (
    <div className="App">
      <form onSubmit={formik.handleSubmit}>
        <input type="text" {...formik.getFieldProps('email')} />
        {formik.errors.email && formik.touched.email && <span>{formik.errors.email}</span>}
        <DatePicker
          onChange={(date: any) => formik.setFieldValue('date', date)}
          selected={formik.values.date}
        />
        {formik.errors.date && formik.touched.date && <span>{formik.errors.date}</span>}

        <input type="submit" value="submit" disabled={!formik.isValid} />
      </form>
    </div>
  );
}

export default App;
